import React , { Component } from 'react'



class App extends Component{
    constructor(){
        super()
        this.state = {
            message: "Welcome Visitor"
        }
    }

changeMessage() {
    this.setState({
        message : "Thankyou for Subscribing"
    })
}

  render(){
    return (
      <div className='App'>

        <h1>{this.state.message}</h1>
        <button onClick={this.state.changeMessage} >Subscribe</button>

      </div>
    )
  }
}

export default App;
